```javascript

function VM() {
  this.stack = []
  this.cs = []
  this.pc = 0
  this.ax = undefined
  this.bx = undefined
}
VM.prototype.run = function(codes) {
  this.cs = codes
  this.pc = 0
  return this.interpret()
}
VM.prototype.interpret = function() {
  var vm = this
  var instruction = vm.cs[vm.pc]
  var op = instruction[0]
  var ops = instruction.slice(1)

  switch (op) {
    case 'jl':
      if (vm.ax < vm.bx) {
        vm.pc = ops[0]
        return vm.interpret()
      }
    case 'inc':
      vm.ax++
      break
    case 'ld':
      vm[ops[0]] = ops[1]
      break
    case 'push':
      vm.stack.push(vm[ops[0]])
      break
    case 'pop':
      vm[ops[0]] = vm.stack.pop()
      break
    case 'arr_push':
      vm.bx.push(vm.ax)
      break
    case 'ret':
      return vm.ax
      break
    default:
      throw 'wtf??'
  }
  vm.pc++
  return vm.interpret()
}

