        for(var j in keyObj){
            if(i==j){
                if(j=='hd2')format='flv';//高清 格式也为flv  
                else format=j;
                var x=0;
                for(var n in keyObj\[j\]){//当第三个key以后为*号 说明为付费视频
                    key=keyObj\[j\]\[n\].k;
